import { localAPI } from 'src/environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "src/services/blockchain.service";
import * as i2 from "src/services/ipfs.service";
import * as i3 from "@angular/common/http";
export class PatientServiceService {
    constructor(blockchainService, ipfsService, http) {
        this.blockchainService = blockchainService;
        this.ipfsService = ipfsService;
        this.http = http;
        this.ipfs = ipfsService.getIPFS();
    }
    //Check if Patient
    async checkIsPatient() {
        return new Promise((resolve, reject) => {
            this.blockchainService.getAccount().then((acc) => {
                this.account = acc;
                this.blockchainService
                    .getContract()
                    .then((r) => {
                    this.contract = r;
                    this.contract.methods
                        .isPat(this.account)
                        .call()
                        .then((result) => {
                        console.log(result);
                        resolve(result);
                    })
                        .catch((err) => {
                        console.log(err);
                        reject(err);
                    });
                })
                    .catch((err) => {
                    console.log(err);
                });
            });
        });
    }
    //get Patient Details
    async getPatientDetails() {
        let id = this.account;
        return new Promise((resolve, reject) => {
            this.blockchainService
                .getAccount()
                .then((acc) => {
                id = acc;
                this.blockchainService
                    .getContract()
                    .then((r) => {
                    this.contract = r;
                    this.contract.methods
                        .getPatInfo(id)
                        .call()
                        .then((result) => {
                        console.log(result);
                        this.http
                            .get(localAPI + result)
                            .subscribe((data) => {
                            console.log(data);
                            resolve(data);
                        });
                    })
                        .catch((err) => {
                        console.log(err);
                        reject(err);
                    });
                })
                    .catch((err) => {
                    reject(err);
                });
            })
                .catch((err) => {
                console.log(err);
            });
        });
    }
    //get patientRecords
    async getPatientRecords() {
        let id = this.account;
        return new Promise((resolve, reject) => {
            this.blockchainService.getAccount().then((acc) => {
                this.account = acc;
                id = this.account;
                this.blockchainService.getContract().then((r) => {
                    this.contract = r;
                    this.contract.methods
                        .viewMedRec(id)
                        .call()
                        .then((result) => {
                        console.log(result);
                        if (result.length >= 1) {
                            this.http
                                .get('http://127.0.0.1:8080/ipfs/' + result)
                                .subscribe((data) => {
                                console.log(data);
                                resolve(data);
                            });
                        }
                        else {
                            resolve(null);
                        }
                    })
                        .catch((err) => {
                        console.log(err);
                        reject(err);
                    });
                });
            });
        });
    }
    //Get Appointments
    async getPatAppointments() {
        return new Promise((resolve, reject) => {
            let check = setInterval(() => {
                this.account = this.blockchainService.account;
                if (this.account) {
                    console.log(typeof this.account);
                    this.http
                        .get('http://localhost:8000/api/getAppointmentPat/' + this.account)
                        .subscribe((result) => {
                        console.log(result.data);
                        resolve(result);
                        clearInterval(check);
                    });
                }
            }, 1000);
        });
    }
    async getAccountBalance() {
        return new Promise((resolve, reject) => {
            this.blockchainService.getBalance().then((b) => {
                resolve(b);
            });
        });
    }
}
